// Format verification result for Build Response Context
const inputData = $input.first();
const verificationResult = inputData.json.verification_result;

// Map teach_back_explanation to stuck for routing (same as Parse Agent Output does for scaffolding)
let category = verificationResult.category;
if (category === 'teach_back_explanation') {
  category = 'stuck';
}

return {
  json: {
    category: category,
    confidence: verificationResult.confidence,
    is_main_problem_attempt: verificationResult.is_main_problem_attempt,
    verification: null,
    agent_reasoning: verificationResult.reasoning,
    tool_used: 'llm_classify_verify',
    original_category: verificationResult.category
  }
};

// Parse LLM extraction and merge with code verification
const llmData = $('Code: Verify Answer').first().json;
const contextData = $('Prepare Agent Context').first().json;

return {
  json: {
    student_message: contextData.student_message,
    current_problem: contextData.current_problem,
    attempt_count: contextData.attempt_count,
    is_scaffolding_active: contextData.is_scaffolding_active,
    scaffolding_depth: contextData.scaffolding_depth,
    scaffolding_last_question: contextData.scaffolding_last_question,
    is_teach_back_active: contextData.is_teach_back_active,
    teach_back_awaiting: contextData.teach_back_awaiting,
    last_tutor_question: contextData.last_tutor_question,
    last_category: contextData.last_category,
    session_id: contextData.session_id,
    student_id: contextData.student_id,
    _session: contextData._session,
    _start_time: contextData._start_time,
    verification_result: {
      category: llmData.final_category,
      is_main_problem_attempt: llmData.is_main_problem_attempt,
      confidence: llmData.confidence,
      reasoning: llmData.reasoning
    }
  }
};

// Code-based answer verification (Stage 2)
const inputData = $input.first();
const llmResponse = inputData.json.message?.content || inputData.json.text || '';
const contextData = $('Prepare Agent Context').first().json;

let llmExtraction;
try {
  llmExtraction = JSON.parse(llmResponse);

  if (!llmExtraction.category) {
    throw new Error('Missing category field');
  }
} catch (error) {
  // Fallback if JSON parsing fails
  llmExtraction = {
    is_answer: false,
    category: 'stuck',
    extracted_value: null,
    confidence: 0.5,
    reasoning: `Parse error: ${error.message}`
  };
}

// If not an answer attempt, pass through unchanged
if (!llmExtraction.is_answer) {
  return {
    json: {
      final_category: llmExtraction.category,
      is_main_problem_attempt: false,
      confidence: llmExtraction.confidence,
      reasoning: llmExtraction.reasoning,
      extracted_value: null
    }
  };
}

// Answer attempt - verify numerically
const studentValue = llmExtraction.extracted_value;
const correctAnswer = contextData.current_problem.correct_answer;

// Parse correct answer
let correctValue;
try {
  correctValue = parseFloat(String(correctAnswer).replace(/[^0-9.\-]/g, ''));
  if (isNaN(correctValue)) {
    throw new Error('Cannot parse correct answer');
  }
} catch (error) {
  // Cannot verify - treat as stuck
  return {
    json: {
      final_category: 'stuck',
      is_main_problem_attempt: false,
      confidence: 0.5,
      reasoning: `Verification error: ${error.message}`,
      extracted_value: studentValue
    }
  };
}

// Check if extracted value is valid
if (studentValue === null || isNaN(studentValue)) {
  return {
    json: {
      final_category: 'stuck',
      is_main_problem_attempt: false,
      confidence: llmExtraction.confidence,
      reasoning: 'Could not extract numeric value from student response',
      extracted_value: null
    }
  };
}

// Numeric comparison
const diff = Math.abs(studentValue - correctValue);
const percentThreshold = Math.abs(correctValue * 0.2); // 20%
const closeThreshold = Math.max(percentThreshold, 0.3); // At least 0.3

const isCorrect = diff < 0.001;
const isClose = !isCorrect && diff <= closeThreshold;

let finalCategory;
if (isCorrect) {
  finalCategory = 'correct';
} else if (isClose) {
  finalCategory = 'close';
} else {
  finalCategory = 'wrong_operation';
}

return {
  json: {
    final_category: finalCategory,
    is_main_problem_attempt: true,
    confidence: llmExtraction.confidence,
    reasoning: `Extracted ${studentValue}, correct is ${correctValue}, diff ${diff.toFixed(2)}`,
    extracted_value: studentValue,
    correct_value: correctValue,
    difference: diff
  }
};

// Verify Main Answer - Read from workflow context ($json)
try {
  // Extract data from workflow context (passed by Prepare Agent Context node)
  const studentMessage = $json.student_message || '';
  const correctAnswer = $json.current_problem?.correct_answer || '';

  if (!studentMessage || !correctAnswer) {
    return 'error: missing student_message or correct_answer in workflow context';
  }

  const studentAnswer = String(studentMessage).toLowerCase().trim();
  const correctAnswerStr = String(correctAnswer).toLowerCase().trim();

  // Extract just numbers (handle negative, decimals, fractions)
  let studentNum = studentAnswer.replace(/[^0-9.\-]/g, '');
  let correctNum = correctAnswerStr.replace(/[^0-9.\-]/g, '');

  if (!studentNum || !correctNum) {
    return 'error: could not parse numbers';
  }

  const studentVal = parseFloat(studentNum);
  const correctVal = parseFloat(correctNum);

  if (isNaN(studentVal) || isNaN(correctVal)) {
    return 'error: invalid numbers';
  }

  // Calculate difference and thresholds
  const diff = Math.abs(studentVal - correctVal);
  const percentThreshold = Math.abs(correctVal * 0.2); // 20%
  const closeThreshold = Math.max(percentThreshold, 0.3); // At least 0.3

  const isCorrect = diff < 0.001;
  const isClose = !isCorrect && diff <= closeThreshold;

  // Return category as string (agent will parse this)
  if (isCorrect) {
    return 'correct';
  } else if (isClose) {
    return 'close';
  } else {
    return 'wrong_operation';
  }

} catch (error) {
  return 'error: ' + error.message + ' | stack: ' + error.stack;
}

// Validate Scaffolding - Read from workflow context ($json)
try {
  // Extract data from workflow context (passed by Prepare Agent Context node)
  const studentResponse = $json.student_message || '';
  const scaffoldingQuestion = $json.scaffolding_last_question || $json.last_tutor_question || '';
  const problemContext = $json.current_problem?.text || '';

  if (!studentResponse) {
    return JSON.stringify({
      status: 'error',
      reasoning: 'Missing student_message in workflow context'
    });
  }

  if (!scaffoldingQuestion) {
    return JSON.stringify({
      status: 'error',
      reasoning: 'Missing scaffolding question in workflow context'
    });
  }

  // Return structured data for agent to evaluate
  // Agent will use its LLM to determine if response is correct
  return JSON.stringify({
    scaffolding_question: scaffoldingQuestion,
    student_response: studentResponse,
    problem_context: problemContext,
    validation_guidelines: {
      accept_text_numbers: true,
      semantic_meaning: 'Accept "minus 2" = "-2" = "negative 2"',
      criteria: 'correct|partially_correct|incorrect'
    }
  });

} catch (error) {
  return JSON.stringify({ 
    status: 'error',
    error: error.message, 
    stack: error.stack 
  });
}

