// Build context for AI Agent
const inputData = $input.item.json;
const sessionData = $('Load Session').item.json;
const session = sessionData.session;

const recentTurns = session.recent_turns || [];
const lastTutorMessage = recentTurns.length > 0 ? recentTurns[recentTurns.length - 1]?.tutor_response || '' : '';
const lastCategory = recentTurns.length > 0 ? recentTurns[recentTurns.length - 1]?.category : null;

// Check if last turn is from current problem (not previous problem)
const lastTurnFromCurrentProblem = recentTurns.length > 0 && 
  !recentTurns[recentTurns.length - 1]?.is_previous_problem;

// Get state from session (preferred) or detect heuristically (fallback)
const scaffoldingState = session.current_problem?.scaffolding || { active: false, depth: 0, last_question: null };
const teachBackState = session.current_problem?.teach_back || { active: false, awaiting_explanation: false };

// Fallback: Detect if we're in scaffolding mode (if state not set)
if (!scaffoldingState.active && recentTurns.length > 0) {
  const hasQuestionMark = lastTutorMessage.includes('?');
  const isScaffoldingQuestion = lastTurnFromCurrentProblem && hasQuestionMark && (
    lastTutorMessage.toLowerCase().includes('what does') ||
    (lastTutorMessage.toLowerCase().includes('what is') && 
      !lastTutorMessage.toLowerCase().includes(session.current_problem.text.toLowerCase())) ||
    lastTutorMessage.toLowerCase().includes('mean') ||
    lastTutorMessage.toLowerCase().includes('think about') ||
    lastTutorMessage.toLowerCase().includes('how') ||
    lastTutorMessage.toLowerCase().includes('can you tell me') ||
    lastTutorMessage.toLowerCase().includes('where is') ||
    lastTutorMessage.toLowerCase().includes('where does') ||
    lastTutorMessage.toLowerCase().includes('show me') ||
    lastTutorMessage.toLowerCase().includes('visualize') ||
    lastTutorMessage.toLowerCase().includes('represent') ||
    lastTutorMessage.toLowerCase().includes('which direction') ||
    lastTutorMessage.toLowerCase().includes("let's start")
  );

  if (isScaffoldingQuestion) {
    scaffoldingState.active = true;
    scaffoldingState.last_question = lastTutorMessage;
  }
}

// Fallback: Detect if we're in teach-back mode (if state not set)
if (!teachBackState.active && lastTurnFromCurrentProblem && lastCategory === 'correct') {
  const isTeachBackQuestion = (
    lastTutorMessage.toLowerCase().includes('explain') ||
    lastTutorMessage.toLowerCase().includes('how did you') ||
    lastTutorMessage.toLowerCase().includes('walk me through') ||
    lastTutorMessage.toLowerCase().includes('tell me how')
  );

  if (isTeachBackQuestion) {
    teachBackState.active = true;
    teachBackState.awaiting_explanation = true;
  }
}

return [{
  json: {
    student_message: inputData.message,
    current_problem: session.current_problem,
    attempt_count: session.current_problem?.attempt_count || 1,
    is_scaffolding_active: scaffoldingState.active,
    scaffolding_depth: scaffoldingState.depth,
    scaffolding_last_question: scaffoldingState.last_question || lastTutorMessage,
    is_teach_back_active: teachBackState.active,
    teach_back_awaiting: teachBackState.awaiting_explanation,
    last_tutor_question: scaffoldingState.active ? (scaffoldingState.last_question || lastTutorMessage) : null,
    last_category: lastCategory,
    session_id: sessionData._session_id,
    student_id: sessionData.student_id,
    _session: session,
    _start_time: sessionData._start_time
  }
}];

// Parse agent response with error handling
const agentData = $input.item.json;

let classification;
try {
  // n8n AI Agent with Structured Output Parser returns data in different formats
  // Try multiple extraction strategies

  // Strategy 1: Direct object with expected fields (most common)
  if (agentData.category && typeof agentData.category === 'string') {
    classification = {
      category: agentData.category,
      is_main_problem_attempt: agentData.is_main_problem_attempt,
      confidence: agentData.confidence,
      reasoning: agentData.reasoning
    };
  }
  // Strategy 2: Nested in 'output' field
  else if (agentData.output) {
    if (typeof agentData.output === 'string') {
      classification = JSON.parse(agentData.output);
    } else if (typeof agentData.output === 'object') {
      classification = agentData.output;
    }
  }
  // Strategy 3: Nested in 'text' field
  else if (agentData.text) {
    if (typeof agentData.text === 'string') {
      classification = JSON.parse(agentData.text);
    } else if (typeof agentData.text === 'object') {
      classification = agentData.text;
    }
  }
  // Strategy 4: Array format
  else if (Array.isArray(agentData)) {
    const firstItem = agentData[0];
    if (firstItem) {
      if (firstItem.category) {
        classification = firstItem;
      } else if (firstItem.output) {
        classification = typeof firstItem.output === 'string' ? JSON.parse(firstItem.output) : firstItem.output;
      } else if (firstItem.text) {
        classification = typeof firstItem.text === 'string' ? JSON.parse(firstItem.text) : firstItem.text;
      }
    }
  }
  // Strategy 5: message.content format (OpenAI)
  else if (agentData.message?.content) {
    if (typeof agentData.message.content === 'string') {
      classification = JSON.parse(agentData.message.content);
    } else {
      classification = agentData.message.content;
    }
  }

  if (!classification || !classification.category) {
    throw new Error('Unable to extract classification with valid category field. Agent data: ' + JSON.stringify(agentData).slice(0, 200));
  }

} catch (error) {
  classification = {
    category: 'stuck',
    confidence: 0.5,
    is_main_problem_attempt: false,
    reasoning: `Parsing error: ${error.message}`,
    tool_used: 'none',
    error: true,
    _raw_data: JSON.stringify(agentData).slice(0, 500)
  };
}

// Map teach_back_explanation to stuck for routing (scaffold_progress gets its own response)
let responseCategory = classification.category;
if (classification.category === 'teach_back_explanation') {
  responseCategory = 'stuck';
}

return [{
  json: {
    category: responseCategory,
    confidence: classification.confidence || 0.5,
    is_main_problem_attempt: classification.is_main_problem_attempt || false,
    verification: classification.verification_result || null,
    agent_reasoning: classification.reasoning || '',
    tool_used: classification.tool_used || 'none',
    original_category: classification.category,
    _parse_error: classification.error || false,
    _raw_sample: classification._raw_data || null
  }
}];

// Build Response Context - Format conversation history for response nodes
const classification = $input.item.json;
const context = $('Prepare Agent Context').item.json;
const session = context._session;

// Format recent turns as chat history string
let chatHistory = '';
if (session.recent_turns && session.recent_turns.length > 0) {
  chatHistory = session.recent_turns.map((turn, i) => {
    return `Student: ${turn.student_message}\nTutor: ${turn.tutor_response}`;
  }).join('\n\n');
}

return [{
  json: {
    // Classification data
    category: classification.category,
    confidence: classification.confidence,
    is_main_problem_attempt: classification.is_main_problem_attempt,
    verification: classification.verification,

    // Context for response generation
    current_problem: context.current_problem,
    message: context.student_message,
    attempt_count: context.attempt_count,
    chat_history: chatHistory,
    is_scaffolding_active: context.is_scaffolding_active,
    scaffolding_depth: context.scaffolding_depth,
    is_teach_back_active: context.is_teach_back_active,
    teach_back_awaiting: context.teach_back_awaiting,

    // Pass through for later nodes
    _session: session,
    _session_id: context.session_id,
    _start_time: context._start_time,
    _classification: classification
  }
}];

// Normalize Input - Transform chat and webhook payloads to consistent format
  const inputData = $input.item.json;

  // Detect source type
  let source = 'unknown';
  let normalizedData = {};

  // Check if this is from Chat Trigger
  if (inputData.chatId || inputData.chat || inputData.sessionId) {
    source = 'chat';

    // Map chat fields to expected format
    normalizedData = {
      session_id: inputData.chatId || inputData.sessionId || inputData.chat?.id || `chat_${Date.now()}`,
      student_id: inputData.userId || inputData.user?.id || inputData.from || 'unknown_user',
      message: inputData.chatInput || inputData.message || inputData.text || inputData.chatMessage || '',

      // Default problem if none provided
      current_problem: inputData.current_problem || {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'chat',
      _original_payload: inputData
    };
  }
  // Check if this is from Webhook Trigger
  else if (inputData.session_id || inputData.student_id || inputData.current_problem) {
    source = 'webhook';

    // Webhook already in correct format, just pass through
    normalizedData = {
      session_id: inputData.session_id,
      student_id: inputData.student_id,
      message: inputData.message,
      current_problem: inputData.current_problem,

      // Metadata
      _source: 'webhook',
      _original_payload: inputData
    };
  }
  // Unknown source - try best guess
  else {
    source = 'unknown';

    normalizedData = {
      session_id: inputData.id || `session_${Date.now()}`,
      student_id: inputData.user || 'unknown',
      message: inputData.chatInput || inputData.message || inputData.text || '',
      current_problem: {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'unknown',
      _original_payload: inputData,
      _warning: 'Could not detect source type, using defaults'
    };
  }

  return {
    json: normalizedData
  };

// Load or initialize session from REDIS
// FIX: Read from Normalize Input, not from Redis node output
const normalizedInput = $('Normalize input').first().json;
const sessionId = normalizedInput.session_id;
const studentId = normalizedInput.student_id;
const currentProblem = normalizedInput.current_problem || {
  id: 'default_problem_1',
  text: 'What is -3 + 5?',
  correct_answer: '2'
};

// Get session from Redis Get node
let session = null;
let sessionFound = false;

try {
  const redisData = $('Redis: Get Session').first().json;
  // Redis returns {key: '...', value: '...'} or {key: '...', propertyName: '...'}
  if (redisData && (redisData.value || redisData.propertyName)) {
    try {
      session = JSON.parse(redisData.value || redisData.propertyName);
      sessionFound = true;
    } catch (error) {
      session = null;
    }
  }
} catch (error) {
  // Redis node failed, will create new session
}

if (!session) {
  // Create new session
  session = {
    session_id: sessionId,
    student_id: studentId,
    created_at: new Date().toISOString(),
    last_active: new Date().toISOString(),
    current_problem: {
      id: currentProblem.id,
      text: currentProblem.text,
      correct_answer: currentProblem.correct_answer,
      attempt_count: 0,
      scaffolding: {
        active: false,
        depth: 0,
        last_question: null
      },
      teach_back: {
        active: false,
        awaiting_explanation: false
      }
    },
    recent_turns: [],
    stats: {
      total_turns: 0,
      problems_attempted: 1,
      problems_solved: 0
    }
  };
}

// Check if problem changed (Hybrid Memory: keep only last 3 turns for continuity)
if (session.current_problem && session.current_problem.id !== currentProblem.id) {
  // Keep last 3 turns from previous problem for continuity
  if (session.recent_turns && session.recent_turns.length > 0) {
    session.recent_turns = session.recent_turns.slice(-3);
    session.recent_turns.forEach(turn => {
      turn.is_previous_problem = true;
    });
  }

  // Reset problem data
  session.current_problem = {
    id: currentProblem.id,
    text: currentProblem.text,
    correct_answer: currentProblem.correct_answer,
    attempt_count: 0,
    scaffolding: {
      active: false,
      depth: 0,
      last_question: null
    },
    teach_back: {
      active: false,
      awaiting_explanation: false
    }
  };
  session.stats.problems_attempted++;
}

// Ensure required fields exist (defensive programming)
if (!session.recent_turns) {
  session.recent_turns = [];
}
if (!session.current_problem) {
  session.current_problem = {
    id: currentProblem.id,
    text: currentProblem.text,
    correct_answer: currentProblem.correct_answer,
    attempt_count: 0,
    scaffolding: { active: false, depth: 0, last_question: null },
    teach_back: { active: false, awaiting_explanation: false }
  };
} else {
  // FIX: Even if session.current_problem exists, ensure text and correct_answer are present
  // This fixes the bug where Redis has incomplete current_problem from previous saves
  if (!session.current_problem.text || !session.current_problem.correct_answer || !session.current_problem.id) {
    session.current_problem.id = session.current_problem.id || currentProblem.id;
    session.current_problem.text = session.current_problem.text || currentProblem.text;
    session.current_problem.correct_answer = session.current_problem.correct_answer || currentProblem.correct_answer;
  }
  // Ensure nested objects exist
  if (!session.current_problem.scaffolding) {
    session.current_problem.scaffolding = { active: false, depth: 0, last_question: null };
  }
  if (!session.current_problem.teach_back) {
    session.current_problem.teach_back = { active: false, awaiting_explanation: false };
  }
}

// Add start time for latency tracking
const startTime = Date.now();

return {
  json: {
    // FIX: Spread normalizedInput (has message field), not Redis output
    ...normalizedInput,
    // Then our explicit fields OVERRIDE
    session: session,
    _session_id: sessionId,
    _start_time: startTime,
    current_problem: currentProblem
  }
};

