// Load or initialize session from workflow static data
const sessionId = $input.item.json.session_id;
const studentId = $input.item.json.student_id;
const currentProblem = $input.item.json.current_problem;

// Get existing sessions from workflow static data
const sessions = $getWorkflowStaticData('global')?.sessions || {};

let session = sessions[sessionId];

// Check if session exists and hasn't expired (30-minute TTL)
if (session) {
  const lastActive = new Date(session.last_active);
  const now = new Date();
  const minutesInactive = (now - lastActive) / 1000 / 60;

  if (minutesInactive > 30) {
    // Session expired - treat as new session
    session = null;
  }
}

if (!session) {
  // Create new session
  session = {
    session_id: sessionId,
    student_id: studentId,
    created_at: new Date().toISOString(),
    last_active: new Date().toISOString(),
    current_problem: {
      id: currentProblem.id,
      text: currentProblem.text,
      correct_answer: currentProblem.correct_answer,
      attempt_count: 0
    },
    recent_turns: [],
    stats: {
      total_turns: 0,
      problems_attempted: 1,
      problems_solved: 0
    }
  };
}

// Check if problem changed
if (session.current_problem.id !== currentProblem.id) {
  session.current_problem = {
    id: currentProblem.id,
    text: currentProblem.text,
    correct_answer: currentProblem.correct_answer,
    attempt_count: 0
  };
  session.stats.problems_attempted++;
}

// Add start time for latency tracking
const startTime = Date.now();

return {
  json: {
    ...$input.item.json,
    session: session,
    _session_id: sessionId,
    _start_time: startTime
  }
};

