// Hybrid Triage: Rule-based + LLM fallback
  const input = $input.item.json;
  const llmOutput = Array.isArray(input) ? input[0] : input;
  const llmResponse = llmOutput.message?.content || llmOutput.text || '{}';

  // Get original message from Load Session
  const loadSessionData = $('Load Session').item.json;
  const originalMessage = loadSessionData.message.trim();

  let triageResult;

  // Rule 1: If it's numeric or a simple mathematical expression, it's an answer
  const isNumeric = /^-?\d+\.?\d*$/.test(originalMessage);
  const isMathExpression = /^-?\d+[\d\s+\-*/().]+\d+$/.test(originalMessage);
  const isWrittenNumber =
  /^(negative|minus)?\s*(zero|one|two|three|four|five|six|seven|eight|nine|ten)$/i.test(originalMessage);

  if (isNumeric || isMathExpression || isWrittenNumber) {
    // Definitely an answer
    triageResult = {
      is_answer: true,
      confidence: 0.99,
      method: 'rule-based'
    };
  }
  // Rule 2: If it contains common non-answer phrases, it's NOT an answer
  else if (/^(what|why|how|help|stuck|don't know|idk)/i.test(originalMessage)) {
    triageResult = {
      is_answer: false,
      confidence: 0.95,
      method: 'rule-based'
    };
  }
  // Otherwise, use LLM classification
  else {
    try {
      triageResult = JSON.parse(llmResponse);
      triageResult.method = 'llm';
    } catch (e) {
      // LLM failed, default to treating as answer if short
      triageResult = {
        is_answer: originalMessage.length < 20,
        confidence: 0.5,
        method: 'fallback'
      };
    }
  }

  return {
    json: {
      ...loadSessionData,
      triage_stage1: triageResult,
      is_answer: triageResult.is_answer
    }
  };

// Verify answer with sanitized eval (no math.js in n8n)
const studentInput = $input.item.json.message;
const correctAnswer = $input.item.json.current_problem.correct_answer;

function verifyAnswer(studentInput, correctAnswer) {
  try {
    // Clean input
    let cleaned = studentInput.trim().toLowerCase();

    // Handle written numbers
    const numberWords = {
      'zero': '0', 'one': '1', 'two': '2', 'three': '3',
      'four': '4', 'five': '5', 'six': '6', 'seven': '7',
      'eight': '8', 'nine': '9', 'ten': '10',
      'negative': '-', 'minus': '-'
    };

    for (let [word, digit] of Object.entries(numberWords)) {
      const regex = new RegExp('\\b' + word + '\\b', 'g');
      cleaned = cleaned.replace(regex, digit);
    }

    // Remove extra spaces
    cleaned = cleaned.replace(/\s+/g, '');

    // Sanitize before eval (security)
    const sanitized = cleaned.replace(/[^0-9+\-*/().]/g, '');
    if (sanitized !== cleaned && cleaned !== '') {
      throw new Error('Invalid characters in input');
    }

    // Evaluate both
    const studentVal = parseFloat(eval(sanitized || '0'));
    const correctVal = parseFloat(eval(correctAnswer));

    // Calculate difference
    const diff = Math.abs(studentVal - correctVal);
    const tolerance = 0.001;

    // Improved close threshold: 20% with min 0.3 and max 2.0
    // This handles both small numbers (0.2) and large numbers (20) appropriately
    const percentThreshold = Math.abs(correctVal * 0.2);
    const closeThreshold = Math.min(Math.max(percentThreshold, 0.3), 2.0);

    return {
      correct: diff < tolerance,
      close: diff >= tolerance && diff < closeThreshold,
      student_value: studentVal,
      correct_value: correctVal,
      difference: diff
    };
  } catch (error) {
    return {
      correct: false,
      close: false,
      error: "Could not parse as number or expression",
      error_message: error.message,
      raw_input: studentInput
    };
  }
}

const verification = verifyAnswer(studentInput, correctAnswer);

return {
  json: {
    ...$input.item.json,
    verification: verification
  }
};

// Stage 2a: Classify answer quality based on verification (rule-based)
const verification = $input.item.json.verification;

let category, confidence;

// Handle verification errors (e.g., unparseable input)
if (verification.error) {
  // Treat as 'stuck' - student might not understand what format to use
  category = 'stuck';
  confidence = 0.8;
} else if (verification.correct) {
  category = 'correct';
  confidence = 0.99;
} else if (verification.close) {
  category = 'close';
  confidence = 0.95;
} else {
  category = 'wrong_operation';
  confidence = 0.85;
}

return {
  json: {
    ...$input.item.json,
    category: category,
    confidence: confidence,
    stage: 'stage2a',
    verification_failed: verification.error ? true : false
  }
};

