// Parse Stage 2b result
const llmResponse = $input.item.json.message?.content || $input.item.json.text || '{}';

// Get data from Parse Triage Result (passed through Stage 2b)
const triageData = $('Parse Triage Result').item.json;
const originalMessage = triageData.message;

let triageResult;

try {
  triageResult = JSON.parse(llmResponse);
} catch (e) {
  // Fallback classification based on keywords
  const messageLower = originalMessage.toLowerCase();
  if (messageLower.includes('what') || messageLower.includes('?')) {
    triageResult = { category: 'conceptual_question', confidence: 0.7, fallback: true };
  } else if (messageLower.includes('don\'t know') || messageLower.includes('help') || messageLower.includes('stuck')) {
    triageResult = { category: 'stuck', confidence: 0.8, fallback: true };
  } else {
    triageResult = { category: 'off_topic', confidence: 0.6, fallback: true };
  }
}

return {
  json: {
    ...triageData,
    category: triageResult.category,
    confidence: triageResult.confidence,
    stage: 'stage2b'
  }
};

// Enrich context with attempt count, escalation level, and formatted chat history
const session = $input.item.json.session;
const category = $input.item.json.category;
const isAnswer = $input.item.json.is_answer;

// Get current attempt count (will increment later if answer)
const attemptCount = session.current_problem.attempt_count + (isAnswer ? 1 : 0);

// Determine escalation level
let escalationLevel;
if (attemptCount <= 1) {
  escalationLevel = 'probe';
} else if (attemptCount === 2) {
  escalationLevel = 'hint';
} else {
  escalationLevel = 'teach';
}

// Format chat history for LLM context
const chatHistory = session.recent_turns.map((turn, i) => {
  return `Turn ${i + 1}:\nStudent: "${turn.student_input}"\nTutor: "${turn.tutor_response}"`;
}).join('\n\n');

return {
  json: {
    ...$input.item.json,
    attempt_count: attemptCount,
    escalation_level: escalationLevel,
    chat_history: chatHistory
  }
};

