// Extract response from whichever LLM node executed
  const llmOutput = $input.item.json;
  const response = llmOutput.message?.content || llmOutput.text || "I'm here to help you learn!";

  // Get data from Enrich Context node
  const enrichContext = $('Enrich Context').item.json;
  const startTime = enrichContext._start_time;
  const latency = Date.now() - startTime;

  // Build turn object
  const turn = {
    student_input: enrichContext.message,
    is_answer: enrichContext.is_answer,
    category: enrichContext.category,
    verification: enrichContext.verification || null,
    tutor_response: response,
    latency_ms: latency,
    timestamp: new Date().toISOString()
  };

  // Update session
  const session = enrichContext.session;

  // Increment attempt count if was an answer (for next turn)
  if (enrichContext.is_answer) {
    session.current_problem.attempt_count++;
  }

  // Mark problem solved if correct
  if (enrichContext.category === 'correct') {
    session.stats.problems_solved++;
  }

  // Add turn to recent history
  session.recent_turns.push(turn);

  // Trim to last 5 turns
  if (session.recent_turns.length > 5) {
    session.recent_turns = session.recent_turns.slice(-5);
  }

  // Update metadata
  session.last_active = new Date().toISOString();
  session.stats.total_turns++;

  // Save session to workflow static data
  const staticData = $getWorkflowStaticData('global');
  if (!staticData.sessions) {
    staticData.sessions = {};
  }
  staticData.sessions[enrichContext._session_id] = session;

  // Return final response
  return {
    json: {
      output: response  // Chat trigger looks for 'output' field
    }
  };

// Normalize Input - Transform chat and webhook payloads to consistent format
  const inputData = $input.item.json;

  // Detect source type
  let source = 'unknown';
  let normalizedData = {};

  // Check if this is from Chat Trigger
  if (inputData.chatId || inputData.chat || inputData.sessionId) {
    source = 'chat';

    // Map chat fields to expected format
    normalizedData = {
      session_id: inputData.chatId || inputData.sessionId || inputData.chat?.id || `chat_${Date.now()}`,
      student_id: inputData.userId || inputData.user?.id || inputData.from || 'unknown_user',
      message: inputData.chatInput || inputData.message || inputData.text || inputData.chatMessage || '',

      // Default problem if none provided
      current_problem: inputData.current_problem || {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'chat',
      _original_payload: inputData
    };
  }
  // Check if this is from Webhook Trigger
  else if (inputData.session_id || inputData.student_id || inputData.current_problem) {
    source = 'webhook';

    // Webhook already in correct format, just pass through
    normalizedData = {
      session_id: inputData.session_id,
      student_id: inputData.student_id,
      message: inputData.message,
      current_problem: inputData.current_problem,

      // Metadata
      _source: 'webhook',
      _original_payload: inputData
    };
  }
  // Unknown source - try best guess
  else {
    source = 'unknown';

    normalizedData = {
      session_id: inputData.id || `session_${Date.now()}`,
      student_id: inputData.user || 'unknown',
      message: inputData.chatInput || inputData.message || inputData.text || '',
      current_problem: {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'unknown',
      _original_payload: inputData,
      _warning: 'Could not detect source type, using defaults'
    };
  }

  return {
    json: normalizedData
  };

// Extract response from whichever LLM node executed
  const llmOutput = $input.item.json;
