// Content-Based Router - handle append mode from Merge
  const inputs = $input.all();

  // Identify which input is which
  let loadSessionData, featureExtractorData;

  for (const input of inputs) {
    const data = input.json;
    if (data.message && data.message.content) {
      // Content Feature Extractor (has OpenAI structure)
      featureExtractorData = data;
    } else if (data.session || data.current_problem) {
      // Load Session data
      loadSessionData = data;
    }
  }

  // Parse JSON from OpenAI
  const jsonString = featureExtractorData.message.content;
  const features = JSON.parse(jsonString);

  const messageType = features.message_type;
  const isScaffolding = loadSessionData.session?.current_problem?.scaffolding?.active || false;
  const teachBackActive = loadSessionData.session?.current_problem?.teach_back?.active || false;

  // Route based on message type
  let route;

  if (teachBackActive) {
    // Check if it's a help request
    if (messageType === 'help_request') {
      route = 'classify_stuck';
    } else {
      route = 'teach_back_validator';
    }
  } else if (messageType === 'answer_attempt') {
    if (isScaffolding) {
      // Check if this might be the main problem answer
      const numericValue = features.numeric_value;
      const correctAnswer = loadSessionData.current_problem.correct_answer;
      const correctValue = parseFloat(String(correctAnswer).replace(/[^0-9.\-]/g, ''));
      const diff = Math.abs(numericValue - correctValue);

      // If answer is close to main problem answer, verify it
      if (diff < Math.max(Math.abs(correctValue * 0.5), 1)) {
        route = 'verify_numeric';
      } else {
        // Otherwise, it's a scaffolding sub-answer
        route = 'validate_conceptual';
      }
    } else {
      route = 'verify_numeric';
    }
  } else if (messageType === 'conceptual_response') {
    route = 'validate_conceptual';
  } else if (messageType === 'question' || messageType === 'help_request') {
    route = 'classify_stuck';
  } else {
    route = 'classify_stuck';
  }

  return {
    json: {
      ...loadSessionData,
      ...features,
      _route: route
    }
  };

// Enhanced Numeric Verifier with Configurable Error Detection

  // Embedded configuration
  const ERROR_DETECTORS = {
    'math_arithmetic_addition': (num1, num2, operation) => {
      return [
        Math.abs(num1) + Math.abs(num2),
        num1 - num2,
        Math.abs(num1 - num2),
        -(num1 + num2)
      ];
    },
    'math_arithmetic_subtraction': (num1, num2, operation) => {
      return [
        num1 + num2,
        Math.abs(num1) + Math.abs(num2),
        num2 - num1,
        Math.abs(num1 - num2)
      ];
    },
    'math_arithmetic_multiplication': (num1, num2, operation) => {
      return [
        num1 + num2,
        Math.abs(num1 * num2),
        -(num1 * num2)
      ];
    },
    'math_arithmetic_division': (num1, num2, operation) => {
      if (num2 === 0) return [];
      return [
        num1 * num2,
        num2 / num1,
        Math.abs(num1 / num2),
        -(num1 / num2)
      ];
    }
  };

  const input = $input.first().json;
  const studentValue = input.numeric_value;
  const correctAnswer = input.current_problem.correct_answer;
  const problemText = input.current_problem.text;

  // Parse correct answer
  let correctValue;
  try {
    correctValue = parseFloat(String(correctAnswer).replace(/[^0-9.\-]/g, ''));
    if (isNaN(correctValue)) throw new Error('Cannot parse correct answer');
  } catch (error) {
    return {
      json: {
        ...input,
        category: 'stuck',
        is_main_problem_attempt: true,
        confidence: 0.5,
        reasoning: `Cannot verify: ${error.message}`
      }
    };
  }

  // Validate numeric value
  if (studentValue === null || isNaN(studentValue)) {
    return {
      json: {
        ...input,
        category: 'stuck',
        is_main_problem_attempt: true,
        confidence: 0.8,
        reasoning: 'Could not extract valid numeric value'
      }
    };
  }

  // Calculate difference
  const diff = Math.abs(studentValue - correctValue);

  // Check if correct
  if (diff < 0.001) {
    return {
      json: {
        ...input,
        category: 'correct',
        is_main_problem_attempt: true,
        confidence: 1.0,
        reasoning: `Student answered ${studentValue}, correct!`
      }
    };
  }

  // Check if close
  const percentThreshold = Math.abs(correctValue * 0.2);
  const closeThreshold = Math.max(percentThreshold, 0.3);
  if (diff <= closeThreshold) {
    return {
      json: {
        ...input,
        category: 'close',
        is_main_problem_attempt: true,
        confidence: 0.9,
        reasoning: `Student answered ${studentValue}, close to ${correctValue} (diff: ${diff.toFixed(2)})`
      }
    };
  }

  // Check if plausible operation error
  const match = problemText.match(/([\-\d.]+)\s*([+\-*/])\s*([\-\d.]+)/);
  if (match) {
    const num1 = parseFloat(match[1]);
    const operation = match[2];
    const num2 = parseFloat(match[3]);

    const operationMap = {
      '+': 'math_arithmetic_addition',
      '-': 'math_arithmetic_subtraction',
      '*': 'math_arithmetic_multiplication',
      '/': 'math_arithmetic_division'
    };

    const detectorKey = operationMap[operation];
    const errorDetector = ERROR_DETECTORS[detectorKey];

    if (errorDetector) {
      const possibleErrors = errorDetector(num1, num2, operation);
      const isOperationError = possibleErrors.some(errorValue =>
        Math.abs(studentValue - errorValue) < 0.001
      );

      if (isOperationError) {
        return {
          json: {
            ...input,
            category: 'wrong_operation',
            is_main_problem_attempt: true,
            confidence: 0.95,
            reasoning: `Student answered ${studentValue}, likely operation misconception`
          }
        };
      }
    }
  }

  // Not correct, not close, not operation error ‚Üí stuck
  return {
    json: {
      ...input,
      category: 'stuck',
      is_main_problem_attempt: true,
      confidence: 0.85,
      reasoning: `Student answered ${studentValue}, not close to ${correctValue}, doesn't match operation errors`
    }
  };

// Semantic Validator - Configurable Pattern Matching

  // Embedded configuration
  const SEMANTIC_PATTERNS = {
    'math_operation_identification': {
      patterns: [
        {
          questionPatterns: ['adding or subtracting', 'add or subtract'],
          expectedKeywords: {
            '+': ['adding', 'add', 'plus', 'addition', 'sum'],
            '-': ['subtracting', 'subtract', 'minus', 'subtraction', 'difference']
          },
          wrongKeywords: {
            '+': ['subtracting', 'subtract', 'minus', 'subtraction'],
            '-': ['adding', 'add', 'plus', 'addition']
          }
        }
      ]
    },
    'math_direction_identification': {
      patterns: [
        {
          questionPatterns: ['direction', 'which way', 'right or left'],
          expectedKeywords: {
            'positive': ['right', 'to the right', 'rightward', 'forward'],
            'negative': ['left', 'to the left', 'leftward', 'backward']
          },
          wrongKeywords: {
            'positive': ['left', 'to the left', 'leftward'],
            'negative': ['right', 'to the right', 'rightward']
          }
        }
      ]
    },
    'math_negative_number_concept': {
      patterns: [
        {
          questionPatterns: ['what does -', 'what is -', 'negative number'],
          expectedKeywords: ['negative', 'less than zero', 'below zero', 'left of zero'],
          wrongKeywords: ['positive', 'greater than zero', 'above zero']
        }
      ]
    }
  };

  const input = $input.first().json;
  const studentMessage = (input.student_message || input.message || '').toLowerCase();
  const scaffoldingQuestion = (input.scaffolding_last_question || '').toLowerCase();
  const keywords = input.keywords || [];
  const problemText = input.current_problem.text || '';
  const numericValue = input.numeric_value;

  let isCorrect = false;
  let reasoning = '';
  let needsLLMValidation = false;

  // PATTERN-BASED VALIDATION
  patternLoop: for (const [patternType, patternConfig] of Object.entries(SEMANTIC_PATTERNS)) {
    for (const pattern of patternConfig.patterns) {
      const questionMatches = pattern.questionPatterns.some(qp =>
        scaffoldingQuestion.includes(qp.toLowerCase())
      );

      if (!questionMatches) continue;

      if (pattern.expectedKeywords) {
        let expectedSet = [];
        let wrongSet = [];

        if (patternType === 'math_operation_identification') {
          if (problemText.includes('+') && !problemText.includes('+ -')) {
            expectedSet = pattern.expectedKeywords['+'];
            wrongSet = pattern.wrongKeywords['+'];
          } else if (problemText.includes('-') && !problemText.includes('+ -')) {
            expectedSet = pattern.expectedKeywords['-'];
            wrongSet = pattern.wrongKeywords['-'];
          }
        } else if (patternType === 'math_direction_identification') {
          if (problemText.match(/\+\s*\d/) || scaffoldingQuestion.includes('positive')) {
            expectedSet = pattern.expectedKeywords['positive'];
            wrongSet = pattern.wrongKeywords['positive'];
          } else if (problemText.match(/\-\s*\d/) || scaffoldingQuestion.includes('negative')) {
            expectedSet = pattern.expectedKeywords['negative'];
            wrongSet = pattern.wrongKeywords['negative'];
          }
        }

        if (expectedSet.length > 0) {
          const hasCorrect = keywords.some(kw => expectedSet.includes(kw));
          const hasWrong = keywords.some(kw => wrongSet.includes(kw));

          if (hasCorrect && !hasWrong) {
            isCorrect = true;
            reasoning = 'Student correctly identified concept';
          } else if (hasWrong) {
            isCorrect = false;
            reasoning = 'Student gave incorrect answer';
          } else {
            needsLLMValidation = true;
          }

          break patternLoop;
        }
      }
    }
  }

  // PROCESS-NUMBER VALIDATION (CORRECTED)
  // If student mentions an exact operand from the problem, treat as partial understanding
  if (reasoning === '' && numericValue !== null && !isNaN(numericValue)) {
    const match = problemText.match(/([\-\d.]+)\s*([+\-*/])\s*([\-\d.]+)/);
    if (match) {
      const num1 = parseFloat(match[1]);
      const num2 = parseFloat(match[3]);

      // Check if student's number matches EXACT operands (not absolute values)
      const matchesNum1 = Math.abs(numericValue - num1) < 0.001;
      const matchesNum2 = Math.abs(numericValue - num2) < 0.001;

      if (matchesNum1 || matchesNum2) {
        // Student identified an exact process number (showing partial understanding)
        isCorrect = true;
        reasoning = 'Student identified process number from problem';
      }
    }
  }

  // FALLBACK
  if (needsLLMValidation || reasoning === '') {
    return {
      json: {
        ...input,
        category: 'stuck',
        is_main_problem_attempt: false,
        confidence: 0.5,
        reasoning: 'Could not validate with patterns, needs LLM',
        _needs_llm_validation: true
      }
    };
  }

  if (isCorrect) {
    return {
      json: {
        ...input,
        category: 'scaffold_progress',
        is_main_problem_attempt: false,
        confidence: 0.95,
        reasoning: reasoning
      }
    };
  } else {
    return {
      json: {
        ...input,
        category: 'stuck',
        is_main_problem_attempt: false,
        confidence: 0.9,
        reasoning: reasoning
      }
    };
  }

// Classify as stuck
  const input = $input.first().json;

  return {
    json: {
      ...input,
      category: 'stuck',
      is_main_problem_attempt: false,
      confidence: 1.0,
      reasoning: 'Student requested help'
    }
  };

// Build Response Context - Merge validator output with session context
  const input = $input.first().json;

  // Input now has EVERYTHING from validators (which spread ...input)
  // Extract session data
  const session = input.session || {};

  // Format recent turns as chat history string
  let chatHistory = '';
  if (session.recent_turns && session.recent_turns.length > 0) {
    chatHistory = session.recent_turns.map((turn, i) => {
      return `Student: ${turn.student_message}\nTutor: ${turn.tutor_response}`;
    }).join('\n\n');
  }

  // Extract scaffolding context from session
  const scaffoldingActive = session.current_problem?.scaffolding?.active || false;
  const scaffoldingLastQuestion = session.current_problem?.scaffolding?.last_question || '';
  const teachBackActive = session.current_problem?.teach_back?.active || false;
  const attemptCount = session.current_problem?.attempt_count || 0;

  return [{
    json: {
      // Pass through everything from validator
      ...input,

      // Add formatted chat history
      chat_history: chatHistory,

      // Add session state for response generation
      is_scaffolding_active: scaffoldingActive,
      scaffolding_last_question: scaffoldingLastQuestion,
      is_teach_back_active: teachBackActive,
      attempt_count: attemptCount,

      // Keep session for Update Session node
      _session: session,
      _session_id: input.session_id || input._session_id
    }
  }];

// Normalize Input - Transform chat and webhook payloads to consistent format
  const inputData = $input.item.json;

  // Detect source type
  let source = 'unknown';
  let normalizedData = {};

  // Check if this is from Chat Trigger
  if (inputData.chatId || inputData.chat || inputData.sessionId) {
    source = 'chat';

    // Map chat fields to expected format
    normalizedData = {
      session_id: inputData.chatId || inputData.sessionId || inputData.chat?.id || `chat_${Date.now()}`,
      student_id: inputData.userId || inputData.user?.id || inputData.from || 'unknown_user',
      message: inputData.chatInput || inputData.message || inputData.text || inputData.chatMessage || '',

      // Default problem if none provided
      current_problem: inputData.current_problem || {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'chat',
      _original_payload: inputData
    };
  }
  // Check if this is from Webhook Trigger
  else if (inputData.session_id || inputData.student_id || inputData.current_problem) {
    source = 'webhook';

    // Webhook already in correct format, just pass through
    normalizedData = {
      session_id: inputData.session_id,
      student_id: inputData.student_id,
      message: inputData.message,
      current_problem: inputData.current_problem,

      // Metadata
      _source: 'webhook',
      _original_payload: inputData
    };
  }
  // Unknown source - try best guess
  else {
    source = 'unknown';

    normalizedData = {
      session_id: inputData.id || `session_${Date.now()}`,
      student_id: inputData.user || 'unknown',
      message: inputData.chatInput || inputData.message || inputData.text || '',
      current_problem: {
        id: 'default_problem_1',
        text: 'What is -3 + 5?',
        correct_answer: '2'
      },

      // Metadata
      _source: 'unknown',
      _original_payload: inputData,
      _warning: 'Could not detect source type, using defaults'
    };
  }

  return {
    json: normalizedData
  };

// Load or initialize session from REDIS
// FIX: Read from Normalize Input, not from Redis node output
const normalizedInput = $('Normalize input1').first().json;
const sessionId = normalizedInput.session_id;
const studentId = normalizedInput.student_id;
const currentProblem = normalizedInput.current_problem || {
  id: 'default_problem_1',
  text: 'What is -3 + 5?',
  correct_answer: '2'
};

// Get session from Redis Get node
let session = null;
let sessionFound = false;

try {
  const redisData = $('Redis: Get Session1').first().json;
  // Redis returns {key: '...', value: '...'} or {key: '...', propertyName: '...'}
  if (redisData && (redisData.value || redisData.propertyName)) {
    try {
      session = JSON.parse(redisData.value || redisData.propertyName);
      sessionFound = true;
    } catch (error) {
      session = null;
    }
  }
} catch (error) {
  // Redis node failed, will create new session
}

if (!session) {
  // Create new session
  session = {
    session_id: sessionId,
    student_id: studentId,
    created_at: new Date().toISOString(),
    last_active: new Date().toISOString(),
    current_problem: {
      id: currentProblem.id,
      text: currentProblem.text,
      correct_answer: currentProblem.correct_answer,
      attempt_count: 0,
      scaffolding: {
        active: false,
        depth: 0,
        last_question: null
      },
      teach_back: {
        active: false,
        awaiting_explanation: false
      }
    },
    recent_turns: [],
    stats: {
      total_turns: 0,
      problems_attempted: 1,
      problems_solved: 0
    }
  };
}

// Check if problem changed (Hybrid Memory: keep only last 3 turns for continuity)
if (session.current_problem && session.current_problem.id !== currentProblem.id) {
  // Keep last 3 turns from previous problem for continuity
  if (session.recent_turns && session.recent_turns.length > 0) {
    session.recent_turns = session.recent_turns.slice(-3);
    session.recent_turns.forEach(turn => {
      turn.is_previous_problem = true;
    });
  }

  // Reset problem data
  session.current_problem = {
    id: currentProblem.id,
    text: currentProblem.text,
    correct_answer: currentProblem.correct_answer,
    attempt_count: 0,
    scaffolding: {
      active: false,
      depth: 0,
      last_question: null
    },
    teach_back: {
      active: false,
      awaiting_explanation: false
    }
  };
  session.stats.problems_attempted++;
}

// Ensure required fields exist (defensive programming)
if (!session.recent_turns) {
  session.recent_turns = [];
}
if (!session.current_problem) {
  session.current_problem = {
    id: currentProblem.id,
    text: currentProblem.text,
    correct_answer: currentProblem.correct_answer,
    attempt_count: 0,
    scaffolding: { active: false, depth: 0, last_question: null },
    teach_back: { active: false, awaiting_explanation: false }
  };
} else {
  // FIX: Even if session.current_problem exists, ensure text and correct_answer are present
  // This fixes the bug where Redis has incomplete current_problem from previous saves
  if (!session.current_problem.text || !session.current_problem.correct_answer || !session.current_problem.id) {
    session.current_problem.id = session.current_problem.id || currentProblem.id;
    session.current_problem.text = session.current_problem.text || currentProblem.text;
    session.current_problem.correct_answer = session.current_problem.correct_answer || currentProblem.correct_answer;
  }
  // Ensure nested objects exist
  if (!session.current_problem.scaffolding) {
    session.current_problem.scaffolding = { active: false, depth: 0, last_question: null };
  }
  if (!session.current_problem.teach_back) {
    session.current_problem.teach_back = { active: false, awaiting_explanation: false };
  }
}

// DEFENSIVE: Force reset teach-back on first turn (prevent Redis corruption)
if (session.recent_turns.length === 0) {
  session.current_problem.teach_back = { active: false, awaiting_explanation: false };
}

// Add start time for latency tracking
const startTime = Date.now();

return {
  json: {
    // FIX: Spread normalizedInput (has message field), not Redis output
    ...normalizedInput,
    // Then our explicit fields OVERRIDE
    session: session,
    _session_id: sessionId,
    _start_time: startTime,
    current_problem: currentProblem
  }
};

// Update session with conversation tracking
  const responseData = $input.first().json;
  const response = responseData.message?.content || responseData.text || "I'm here to help you learn!";

  // Get context from Build Response Context1 (always has full context)
  const contextData = $('Build Response Context1').first().json;

  const session = contextData._session || contextData.session;
  const category = contextData.category;

  // Only increment attempt_count for main problem attempts
  if (contextData.is_main_problem_attempt) {
    session.current_problem.attempt_count++;
  }

  // STATE TRANSITIONS

  // 1. SCAFFOLDING STATE MANAGEMENT
  if (category === 'stuck' && !contextData.is_scaffolding_active && !contextData.is_teach_back_active) {
    session.current_problem.scaffolding = {
      active: true,
      depth: 1,
      last_question: response
    };
  } else if (category === 'scaffold_progress') {
    if (session.current_problem.scaffolding) {
      session.current_problem.scaffolding.depth++;
      session.current_problem.scaffolding.last_question = response;
    }
  } else if (category === 'stuck' && contextData.is_scaffolding_active && !contextData.is_teach_back_active) {
    if (session.current_problem.scaffolding) {
      session.current_problem.scaffolding.last_question = response;
    }
  } else if (category === 'correct' && contextData.is_scaffolding_active) {
    session.current_problem.scaffolding = {
      active: false,
      depth: 0,
      last_question: null
    };
  }

  // 2. TEACH-BACK STATE MANAGEMENT
  if (category === 'correct' && !contextData.is_teach_back_active) {
    session.current_problem.teach_back = {
      active: true,
      awaiting_explanation: true
    };
  } else if (category === 'teach_back_explanation') {
    session.current_problem.teach_back = {
      active: false,
      awaiting_explanation: false
    };
  }

  // Mark solved if correct
  if (category === 'correct') {
    session.stats.problems_solved++;
  }

  // Track conversation in recent_turns
  if (!session.recent_turns) {
    session.recent_turns = [];
  }

  session.recent_turns.push({
    student_message: contextData.student_message || contextData.message,
    tutor_response: response,
    category: category,
    timestamp: new Date().toISOString()
  });

  // Keep only last 15 turns
  if (session.recent_turns.length > 15) {
    session.recent_turns = session.recent_turns.slice(-15);
  }

  session.last_active = new Date().toISOString();
  session.stats.total_turns++;

  return [{
    json: {
      output: response,
      _session_id: contextData._session_id || contextData.session_id,
      _session_for_redis: session
    }
  }];

// Parse synthesis detector output
const llmResponse = $json.message?.content || $json.text || $json.response || '';
const parsed = JSON.parse(llmResponse);

// Get original data from Build Response Context (contains category, etc.)
const originalData = $('Build Response Context1').first().json;

return {
  json: {
    ...originalData,              // Preserve all original fields including category
    ...parsed,                    // Add synthesis fields
    synthesis_action: parsed.action,
    synthesis_hint: parsed.synthesis_hint || ''
  }
};

// Teach-Back Validator
  const input = $input.first().json;
  const studentMessage = (input.student_message || input.message || '').toLowerCase();
  const numericValue = input.numeric_value;
  const correctAnswer = input.current_problem.correct_answer;

  // Detect help requests
  const helpPatterns = ["i don't know", "dont know", "not sure", "help me", "stuck"];
  const isHelpRequest = helpPatterns.some(p => studentMessage.includes(p));

  if (isHelpRequest) {
    return { json: { ...input, category: 'stuck', is_main_problem_attempt: false, confidence: 1.0, reasoning: `Help request 
  during teach-back` }};
  }

  // Detect explanation attempts (EXPANDED)
  const explanationPatterns = [
    'i followed', 'i did', 'i got', 'because', 'i think', 'first', 'then',
    'i counted', 'i used', 'i started', 'i imagined', 'i pictured', 'i visualized',
    'i saw', 'i drew', 'number line', 'steps', 'it was easy', 'it was'
  ];
  const hasExplanation = explanationPatterns.some(p => studentMessage.includes(p));

  if (hasExplanation || numericValue !== null) {
    return { json: { ...input, category: 'teach_back_explanation', is_main_problem_attempt: false, confidence: 0.9, reasoning:
   `Explanation attempt` }};
  }

  return { json: { ...input, category: 'stuck', is_main_problem_attempt: false, confidence: 0.7, reasoning: `Ambiguous` }};
